import React,{Component} from "react"
import { NewsItem } from "./NewsItem"

export class News extends Component{
  
    constructor(){
        super();
            console.log("News Constructor")
        this.state = {
            articles : [],
            loading : true
        } 
    }

    async componentDidMount(){
        let url = "https://newsapi.org/v2/top-headlines?country=us&apiKey=8a80ebbbf2284877812f82a12945f594"
        let data = await fetch(url)
        let parsedata = await data.json()
        console.log(parsedata)
        this.setState({articles: parsedata.articles})
    }
    PreviousClickHandler = () =>{}
    NextClickHandler = () =>{}
    render(){
        return( 
            <div className="container my-3">
            <h2>Top Headlines</h2>
            <div className="row" >
                {this.state.articles.map((element)=>{
                  return <div className="col-md-4" key={element.url}>
                <NewsItem title={element.title} description={element.description} imageUrl={element.urlToImage} newsUrl = {element.url}/>
            </div>})}
            <div className = "container d-flex justify-content-between">
            <button disabled type="button" className="btn btn-dark" onClick={this.PreviousClickHandler}>&larr; Previous</button>   
            <button type="button" className="btn btn-dark" onClick={this.NextClickHandler}>Next &rarr;</button>
            </div>
            </div>
            </div>
        )
    }
}
