import React,{Component} from "react"
import { NewsItem } from "./NewsItem"

export class News extends Component{
       
    constructor(){
        super()
            console.log("News Constructor")
        this.state = {
            articles : [],
            loading : true,
            page : 1
        } 
    }

    async componentDidMount(){
        let url = "https://newsapi.org/v2/top-headlines?country=se&cattegory=technology&apiKey=8a80ebbbf2284877812f82a12945f594&page=1&pageSize = 20"
        let data = await fetch(url)
        let parsedata = await data.json()
        console.log(parsedata)
        this.setState({
            articles: parsedata.articles,
            totalResults : parsedata.totalResults})
    }
    
    PreviousClickHandler = async () =>{
        let url = `https://newsapi.org/v2/top-headlines?country=us&apiKey=8a80ebbbf2284877812f82a12945f594&page=${this.state.page-1}&pageSize = 20`
        let data = await fetch(url)
        let parsedata = await data.json()
        console.log(parsedata)
        this.setState({
            articles: parsedata.articles,
            page : this.state.page - 1})
    }
    NextClickHandler = async () =>{
        if (this.state.page + 1 > Math.ceil(this.state.totalResults/20)){}
        else{
        let url = `https://newsapi.org/v2/top-headlines?country=us&apiKey=8a80ebbbf2284877812f82a12945f594&page=${this.state.page + 1}&pageSize = 20`
        let data = await fetch(url)
        let parsedata = await data.json()
        console.log(parsedata)
        this.setState({
            articles: parsedata.articles,
            page : this.state.page + 1})
        }
    }
    render(){
        return( 
            <div className="container my-3">
                h1.text
            <h2>Top Headlines</h2>
            <div className="row" >
                {this.state.articles.map((element)=>{
                  return <div className="col-md-4" key={element.url}>
                <NewsItem title={element.title} description={element.description} imageUrl={element.urlToImage} newsUrl = {element.url}/>
            </div>})}
            <div className = "container d-flex justify-content-between">
            <button disabled ={this.state.page<=1} type="button" className="btn btn-dark" onClick={this.PreviousClickHandler}>&larr; Previous</button>   
            <button disabled ={this.state.page<=1} type="button" className="btn btn-dark" onClick={this.NextClickHandler}>Next &rarr;</button>
            </div>
            </div>
            </div>
        )
    }
}
