import React,{Component} from "react"
import { NewsItem } from "./NewsItem"
import Spinner from "./Spinner"
import PropTypes from "prop-types"


export class News extends Component{
    static defaultProps ={
        country : "sa",
        pageSize : 10,
        category : "general"
    }

    static propTypes = {
        country : PropTypes.string,
        pageSize : PropTypes.number,
        category : PropTypes.string

    }

    capitalizeFirstLetter = (string) => {
        return string.charAt(0).toUpperCase() + string.slice(1);
    } 

       
    constructor(props){
        super(props)
            console.log("News Constructor")
        this.state = {
            articles : [],
            loading : false,
            page : 1
        }
        document.title = `${thiscapitalizeFirstLetter .this.props.category} - DailyNews` 
    }


    UpdateNews= async () =>{
        const url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=6c29877884fa4951b737574bfa3fa776&page=${this.state.page}&pageSize=${this.props.pageSize}`
        let data = await fetch(url)
        this.setState({loading : true})
        let parsedata = await data.json()
        this.setState({
            articles: parsedata.articles,
            totalResults : parsedata.totalResults,
            loading : true
        })
    }   
    async componentDidMount(){
        this.UpdateNews()
    } 

    PreviousClickHandler = async () =>{
        this.setState({
            page : this.state.page - 1
        })
        this.UpdateNews()
    }
    NextClickHandler = async () =>{
        this.setState({
            page : this.state.page + 1
        })
        this.UpdateNews()
        }
    render(){
        return( 
            <div className="container my-3">
            <h1 className="text-center">DailyNews - Top Headlines from {this.props.category} </h1>
            {this.state.loading && <Spinner/>}
            <div className="row" >
                {this.state.articles.map((element)=>{
                  return <div className="col-md-4" key={element.url}>
                <NewsItem title={element.title} description={element.description} imageUrl={element.urlToImage} newsUrl = {element.url} date={element.publishedAt} author={element.author}  source={element.source.name}/>
            </div>})}
            <div className = "container d-flex justify-content-between">
            <button disabled ={this.state.page<=1} type="button" className="btn btn-dark" onClick={this.PreviousClickHandler}>&larr; Previous</button>   
            <button disabled = {this.state.page + 1 > Math.ceil(this.state.totalResults/this.props.pageSize)} type="button" className="btn btn-dark" onClick={this.NextClickHandler}>Next &rarr;</button>
            </div>
            </div>
            </div>
        )
    }
}
